import React,{useEffect,useState} from 'react'

import './PlayButton.scss'
const PlayButton =()=>{
    function calTimer(time){
        let min=Math.floor(time/60)
        let sec=time%60
        return [`${min}:${sec}`,time]

    }
    const[currentTime, setCurrentTime]=useState(['0:00',0]);
    const [lengthTime,setLengthTime]=useState(calTimer(1000));
    return(
        <div className='music-controller'>
            <div className='ranger-slider'>
                <input onInput={(e)=>calTimer(this.value)} defaultValue={currentTime[1]} type="range" min='0' max={lengthTime[1]}  className="slider" id="myRange"/>
            </div>
            <div className='timer flex'>
                <div className='timer-current'>{currentTime[0]}</div>
                <div className="timer-length">{lengthTime[0]}</div>
            </div>
        </div>
    )
}
export default PlayButton
