import React,{useEffect,useState} from 'react'
import {AiFillPlayCircle} from 'react-icons/all'
import './PlayButton.scss'
const PlayButton =()=>{
    function calTimer(time){
        let min=Math.floor(time/60)
        let sec=time%60
        return `${min}:${sec}`

    }
    const[currentTime, setCurrentTime]=useState(0);
    const [changeTime,setChangeTime]=useState('0:00');
    const [lengthTime,setLenghTime]=useState([calTimer(200),200]);
    const [isPlay,setIsPlay]=useState(false)
    useEffect(
        ()=>{
            console.log(currentTime)
            setChangeTime(calTimer(currentTime))
        }
        ,[currentTime]
    ,currentTime)

    return(
        <div className='music-controller'>
            <div className='ranger-slider'>
                <input onInput={(e)=>setCurrentTime(e.target.value)} defaultValue='0' type="range" min='0' max={lengthTime[1]}  className="slider" id="myRange"/>
            </div>
            <div className='timer flex'>
                <div className='timer-current'>{changeTime}</div>
                <div className="timer-length">{lengthTime[0]}</div>
            </div>
            <div className='play-btn'>
                <AiFillPlayCircle className='play-btn-icon my-2 text-5xl bottom-2 bottom-3 sm:text-6xl'/>
            </div>
        </div>
    )
}
export default PlayButton
