import React,{useEffect,useState} from 'react'
import {AiFillPlayCircle,AiFillPauseCircle,BiSkipNext} from 'react-icons/all'
import './PlayButton.scss'
const PlayButton =()=>{
    function calTimer(time){
        let min=Math.floor(time/60)
        let sec=time%60
        if(sec<10) sec='0'+sec
        return `${min}:${sec}`

    }
    const[currentTime, setCurrentTime]=useState(0);
    const [changeTime,setChangeTime]=useState('0:00');
    const [lengthTime,setLenghTime]=useState([calTimer(200),200]);
    const [isPlay,setIsPlay]=useState(false)
    useEffect(
        ()=>{
            console.log(currentTime)
            setChangeTime(calTimer(currentTime))
        }
        ,[currentTime]
    ,currentTime)

    return(
        <div className='music-controller'>
            <div className='ranger-slider'>
                <input onInput={(e)=>setCurrentTime(e.target.value)} defaultValue='0' type="range" min='0' max={lengthTime[1]}  className="slider" id="myRange"/>
            </div>
            <div className='timer flex justify-center'>
                <div className='timer-current'>{changeTime}</div>
                <div className='current-song '>Why not me</div>
                <div className="timer-length">{lengthTime[0]}</div>
            </div>
            <div className='play-controller flex justify-center'>
                <BiSkipNext className='next-btn my-auto  bottom-2 text-5xl flex-initial' />
                {isPlay==true
                    ? <AiFillPlayCircle onClick={()=>setIsPlay(!isPlay)} className='  play-btn  my-auto  bottom-2 text-5xl flex-initial'/>
                    : <AiFillPauseCircle onClick={()=>setIsPlay(!isPlay)} className='  play-btn  my-auto bottom-2 text-5xl flex-initial'/>}
                <BiSkipNext className='next-btn my-auto  bottom-2 text-5xl flex-initial' />
            </div>
        </div>
    )
}
export default PlayButton
