import React,{useEffect,useState} from 'react'

import './PlayButton.scss'
const PlayButton =()=>{
    function calTimer(time){
        let min=Math.floor(time/60)
        let sec=time%60
        return `${min}:${sec}`

    }
    const[currentTime, setCurrentTime]=useState(0);
    const [lengthTime,setLengthTime]=useState([calTimer(1000),1000]);

    return(
        <div className='music-controller'>
            <div className='ranger-slider'>
                <input onChange={calTimer} type="range" min={currentTime} max={lengthTime[0]}  className="slider" id="myRange"/>
            </div>
            <div className='timer flex'>
                <div className='timer-current'>{currentTime}</div>
                <div className="timer-length">{lengthTime}</div>
            </div>
        </div>
    )
}
export default PlayButton
