import React,{useState,useEffect} from 'react'
import './MusicPlayerCard.scss'
import PlayButton from "../PlayButton/PlayButton";
import audios from "../Song/Song";
import {AiFillPauseCircle, AiFillPlayCircle, BiSkipNext, BiSkipPrevious, FaRandom, FiRepeat} from "react-icons/all";
const MusicPlayerCard =()=>{
    const img1='https://www.fillmurray.com/500/500';
    const [song]=useState(audios)
    const [index,setIndex]=useState([]);
    const [currentsong,setCurrentsong]=useState()

    function clickCard(numb) {
        console.log(numb)
        setIndex(numb);
        setCurrentsong(song[numb])
    }
    useEffect(()=>{
        setCurrentsong(song[index])
        console.log('index'+index)

    },[index])




    /*ddddddddddddddddddddddddddddddddddddddddddddddddddd*/
    function calTimer(time){
        let min=Math.floor(time/60)
        let sec=time%60
        if(sec<10) sec='0'+sec
        return `${min}:${sec}`

    }
    const[currentTime, setCurrentTime]=useState(0);
    const [changeTime,setChangeTime]=useState('0:00');
    const [lengthTime,setLenghTime]=useState([calTimer(200),200]);
    const [isPlay,setIsPlay]=useState(false)
    const[isRepeat,setIsRepeat]=useState(false)
    const[isRandom,setIsRandom]=useState(false)
    const[width,setWidth]=useState(0)
    let style={
        width: `${width}%`
    }
    let clickedStyle={color:'#ef1e21'}
    let notClickedStyle={color:'#cccccc'}
    const [audio,setAudio]=useState()
    useEffect(
        ()=>{
            setWidth(currentTime/lengthTime[1]*100)
            setChangeTime(calTimer(currentTime))
        }
        ,[currentTime])
    useEffect(
        ()=>{
            if(currentsong!=undefined) isPlay ? audio.play(): audio.stop();
        },[isPlay])
    return(
        <div>
            <div className="card">
                <div className="songs-wrapper">
                    <div className="row">
                        {song.map((item,numb)=>
                            <div className="col s12 m6 l4 x3">
                                <div className={`${index}`==`${numb}`?`playing-song song `:`song`}>
                                    <div className="song-image" style={{backgroundImage:`url(${img1})`}}></div>
                                    <div className="song-info">
                                        <div onClick={()=>clickCard(numb)} className="song-title">{item.tilte}</div>
                                        <div className="song-artist">{item.author}</div>
                                        <div className="song-time">{numb}</div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            <div className='music-controller'>
                <div className='red-line' style={style}></div>
                <div className='ranger-slider'>
                    <input onChange={(e)=>setCurrentTime(e.target.value)} defaultValue='0' type="range" min='0' max={lengthTime[1]}  className="slider" id="myRange"/>
                </div>
                <div className='timer flex justify-center'>
                    <div className='timer-current'>{changeTime}</div>
                    <div className='current-song '>Why not me</div>
                    <div className="timer-length">{lengthTime[0]}</div>
                </div>
                <div className='play-controller flex justify-center'>
                    <BiSkipPrevious className='skip-btn prev-btn bottom-2 text-5xl flex-initial' />
                    {isPlay==false
                        ? <AiFillPlayCircle onClick={()=>{setIsPlay(!isPlay)}} className='  play-btn    text-5xl flex-initial'/>
                        : <AiFillPauseCircle onClick={()=>setIsPlay(!isPlay)} className='  play-btn    text-5xl flex-initial'/>}
                    <BiSkipNext className='skip-btn next-btn text-5xl flex-initial' />
                </div>
                <div className='other-btn my-auto text-xl flex justify-center'>
                    <FaRandom onClick={()=>setIsRandom(!isRandom)} className='random-btn  m-2 ' style={isRandom ? clickedStyle : notClickedStyle}/>
                    <FiRepeat onClick={()=>setIsRepeat(!isRepeat)} className='repeat-btn  m-2  ' style={isRepeat ? clickedStyle : notClickedStyle}/>
                </div>
            </div>
        </div>
    )
}
export default MusicPlayerCard
