import React,{useEffect,useState} from 'react'
import {AiFillPlayCircle,AiFillPauseCircle,BiSkipNext,BiSkipPrevious,FaRandom,FiRepeat} from 'react-icons/all'
import './PlayButton.scss'
const PlayButton =()=>{
    function calTimer(time){
        let min=Math.floor(time/60)
        let sec=time%60
        if(sec<10) sec='0'+sec
        return `${min}:${sec}`

    }
    const[currentTime, setCurrentTime]=useState(0);
    const [changeTime,setChangeTime]=useState('0:00');
    const [lengthTime,setLenghTime]=useState([calTimer(200),200]);
    const [isPlay,setIsPlay]=useState(false)
    const[width,setWidth]=useState(0)
    let style={
        width: `${width}%`
    }

    useEffect(
        ()=>{
            setWidth(currentTime/lengthTime[1]*100)
            setChangeTime(calTimer(currentTime))
            console.log(width)
        }
        ,[currentTime]
    ,currentTime)

    return(
        <div className='music-controller'>
            <div className='red-line' style={style}></div>
            <div className='ranger-slider'>
                <input onChange={(e)=>setCurrentTime(e.target.value)} defaultValue='0' type="range" min='0' max={lengthTime[1]}  className="slider" id="myRange"/>
            </div>
            <div className='timer flex justify-center'>
                <div className='timer-current'>{changeTime}</div>
                <div className='current-song '>Why not me</div>
                <div className="timer-length">{lengthTime[0]}</div>
            </div>
            <div className='play-controller flex justify-center'>
                <BiSkipPrevious className='skip-btn prev-btn bottom-2 text-5xl flex-initial' />
                {isPlay==true
                    ? <AiFillPlayCircle onClick={()=>setIsPlay(!isPlay)} className='  play-btn    text-5xl flex-initial'/>
                    : <AiFillPauseCircle onClick={()=>setIsPlay(!isPlay)} className='  play-btn    text-5xl flex-initial'/>}
                <BiSkipNext className='skip-btn next-btn text-5xl flex-initial' />
            </div>
            <div className='other-btn my-auto text-xl flex justify-center'>
                <FaRandom className='random-btn m-2 '/>
                <FiRepeat className='repeat-btn m-2  '/>
            </div>
        </div>
    )
}
export default PlayButton
